import { useEffect, useRef, useState } from "react";
import CountUp from "react-countup";
import styled from "styled-components";

const CounterSection = styled.section`
  display: flex;
  flex-direction: column;
  text-align: center;
  padding: 2rem;

  @media (max-width: 768px) {
    padding: 1rem;
  }
`;

const VisionSection = styled.section`
  padding: 2rem;
  background-color: #fff;
  text-align: center;

  @media (max-width: 768px) {
    padding: 1rem;
  }
`;

const Title = styled.div`
  margin-bottom: 2rem;
`;

const Heading = styled.h1`
  font-size: 2.5rem;

  @media (max-width: 768px) {
    font-size: 1.8rem;
  }
`;

const CounterRow = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 2rem;
  font-size: 1.8rem;
`;

const CounterColumn = styled.div`
  flex-basis: 30%;
  text-align: center;

  @media (max-width: 768px) {
    flex-basis: 45%;
  }
`;

const NumberWrapper = styled.div`
  font-size: 2rem;
  font-weight: bold;

  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const Text = styled.span`
  font-size: 1.2rem;

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const VisionContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

function Counter({ className, ...rest }) {
  const [viewPortEntered, setViewPortEntered] = useState(false);
  const [hasCounted, setHasCounted] = useState(false);
  const counterColumnsRef = useRef([]);
  const observerRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting && !hasCounted) {
            setViewPortEntered(true);
            setHasCounted(true); // Set hasCounted to true to prevent future counts
          }
        });
      },
      {
        rootMargin: "0px",
        threshold: 0.5,
      }
    );

    const counterColumns = document.querySelectorAll(".counter-column");
    counterColumns.forEach((column, index) => {
      column.dataset.index = index;
      observer.observe(column);
      counterColumnsRef.current[index] = false;
    });

    observerRef.current = observer;

    return () => {
      observerRef.current.disconnect();
    };
  }, [hasCounted]);

  return (
    <>
      <VisionSection>
        <Title>
          <Heading>Vision</Heading>
        </Title>
        <CounterRow>
  {[
    { count: 500000, text: "To serve 500,000 households" },
    { count: 10000, text: "To Create Jobs" },
  ].map(({ text, count }, index) => (
    <CounterColumn key={index} className="counter-column">
      <VisionContent>
        <NumberWrapper>
          <strong data-number={count}>
            <CountUp
              {...rest}
              start={viewPortEntered ? 0 : 0}
              end={viewPortEntered ? count : 0}
              duration={3}
              prefix="+"
            >
              {({ countUpRef }) => (
                <span className="number" ref={countUpRef} />
              )}
            </CountUp>
          </strong>
        </NumberWrapper>
        <Text>{text}</Text>
      </VisionContent>
    </CounterColumn>
  ))}
</CounterRow>
      </VisionSection>

      <CounterSection className={`counter ${className}`}>
        <Title>
          <Heading>What makes us unique</Heading>
        </Title>

        <CounterRow>
          {[
            { count: 500, text: "Health care Professionals" },
            { count: 5, text: "Years Experience" },
            { count: 10000, text: "Served Families" },
          ].map(({ count, text }, index) => (
            <CounterColumn key={index} className="counter-column">
              <div className="counter-content">
                <NumberWrapper>
                  <strong data-number={count}>
                    <CountUp
                      {...rest}
                      start={viewPortEntered ? 0 : 0}
                      end={viewPortEntered ? count : 0}
                      duration={3}
                      prefix="+"
                    >
                      {({ countUpRef }) => (
                        <span className="number" ref={countUpRef} />
                      )}
                    </CountUp>
                  </strong>
                </NumberWrapper>
                <Text>{text}</Text>
              </div>
            </CounterColumn>
          ))}
        </CounterRow>
      </CounterSection>
    </>
  );
}

export default Counter;
