import React, { useState, useEffect } from "react";
import "animate.css";
import { useWindowWidth } from "react-responsive";

export const Testimonials = (props) => {

  const [isVisible, setIsVisible] = useState(false);
  const [rowOneVisible, setRowOneVisible] = useState(false);  
  const [rowTwoVisible, setRowTwoVisible] = useState(false);

  // Add expanded state
  const [expanded, setExpanded] = useState(false);

  const isMobile = useWindowWidth() < 600;

  useEffect(() => {
    // existing intersection observer logic
  }, [])

  return (
    <div id="testimonials">

      <div className="container">

        // existing section title and content 

      </div>

      {props.data?.map((d, i) => (

        <div 
          key={`${d.name}-${i}`}
          className={`col-md-6 ${
            rowOneVisible || rowTwoVisible  
              ? "animate__animated animate__slideInLeft/Right"
              : "testimonial-hidden"
          }`}>

          <div className="testimonial">

            <div className="testimonial-image">
              <img src={d.img} alt="" />
            </div>

            <div className="testimonial-content">



               {!isMobile && (

                 <p>{d.text}</p>

               )}

              <div className="testimonial-meta">  
                - {d.name}  
              </div>

            </div>

          </div>

        </div>

      ))}

    </div>

  );
};
